CREATE TABLE City (id VARCHAR(255) NOT NULL, departement VARCHAR(3) NOT NULL, latitude DOUBLE NOT NULL, longitude DOUBLE NOT NULL, metaphone VARCHAR(22) NOT NULL, name VARCHAR(45) NOT NULL, population INTEGER, postal_code VARCHAR(255) NOT NULL, real_name VARCHAR(45) NOT NULL, simple_name VARCHAR(45) NOT NULL, slug VARCHAR(45) NOT NULL, soundex VARCHAR(20) NOT NULL, PRIMARY KEY (id), UNIQUE U_CITY_SLUG (slug)) ENGINE = innodb;